import { React, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.scss';
import Nav from './components/Nav';
import Footer from './components/Footer';
import logo from './img/icon1.png';
import ex_png from './img/example_1.png'
import Table from './components/Table';
import Modal from 'react-modal';

const modalStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    width: '600px',
    transform: 'translate(-50%, -50%)',
  },
};

// CSS 스타일 직접 포함
const pdfUploadStyle = {
  textAlign: 'center',
};

const Home = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const navigate = useNavigate();
  const handleEnter = () => {
    navigate('/chatbot');
  };
  const welcome_msg = "데이터분석기초 챗봇에 오신걸 환영합니다 🎈\n저는 여러분의 수업을 도와줄 챗봇 DAC라고 합니다.\n 잘 부탁드립니다 :)";
  const [text, setText] = useState('');
  const [count, setCount] = useState(0);

  useEffect(()=> {
    const interval = setInterval(()=> {
      setText(text + welcome_msg[count]);
      setCount(count + 1);
    }, 50);
    if(count === welcome_msg.length){
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  })

  const iCampus_link = "https://canvas.skku.edu/courses/46987";

  const handleFileUpload = (event) => {
    // 파일 업로드 처리를 여기에 작성
    console.log("파일 업로드가 완료되었습니다.");
  };

  return (
    <div>
      <Nav/>
      <div className="home-container">
        <div className="logo">
          <img className='logoImage' src={logo} width='450' height='450' />
        </div>
        <div className="welcome-message">{text}</div>
        <div className='table'>
          <button className="enter-button" onClick={() => { window.open(iCampus_link); }}>i-Campus</button>
          <button className="enter-button" onClick={() => setModalIsOpen(true)}>입장하기</button>
          <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)} style={modalStyles}>
            <div className='modal-container'>
              <Nav/>
              <div style={pdfUploadStyle}> {/* PDF 파일 업로드 스타일 적용 */}
                <form action="/upload/" method="post" encType="multipart/form-data" onSubmit={handleFileUpload}>
                  <label htmlFor="file">PDF 파일 선택:</label><br/>
                  <input type="file" id="file" name="files" accept=".pdf"/><br/><br/>
                  <input type="submit" value="업로드"/>
                </form>
              </div>
              <div className='button-area'>
                <button className="enter-button" onClick={() => setModalIsOpen(false)}>Back</button>
                <button className="enter-button" onClick={handleEnter}>Start</button>
              </div>
            </div>
          </Modal>
        </div>
        <Footer/>
      </div>
    </div>
  );
};

export default Home;
